var convnetjs = require("convnetjs");
var fs = require("fs");
var layer_defs = [];
var net = new convnetjs.Net();
var stream = require("querystream");
var mongodb = require('mongodb')
  , mongoose = require('mongoose');

var dbUrl = "localhost"
  , dbName = "test";

mongoose.connect(dbUrl, dbName);
var db = mongoose.connection;
db.on('error', console.error.bind(console, 'connection error:'));
db.once('open', function callback() {
  console.log('Connected to DB');
});

exports.mongoose = mongoose;

var schm = mongoose.Schema({
    jing_l: {type: String},
    fang_l: {type: String},
    zheng: {type: Array}
});

layer_defs.push({type:'input', out_sx:32, out_sy:32, out_depth:1});
layer_defs.push({type:'fc', num_neurons:200, activation:'relu'}); 
layer_defs.push({type:'fc', num_neurons:200, activation:'relu'}); 
//layer_defs.push({type:'conv', sx:5, filters:8, stride:1, pad:2, activation:'relu'});
//layer_defs.push({type:'pool', sx:2, stride:2});
//layer_defs.push({type:'conv', sx:5, filters:16, stride:1, pad:2, activation:'relu'});
//layer_defs.push({type:'pool', sx:3, stride:3});
layer_defs.push({type:'softmax', num_classes:6});
		
var data, labels, N;
var jing = ["太阳", "阳明", "少阳", "厥阴", "少阴", "太阴"];

init_net();
function init_net() {
	data = [];
	labels = [];
    var student = mongoose.model('learning', schm);
	student.find({}, function(err, recs) {
    	if (!err) {
		    var findj = false;
    		recs.forEach(function(mp) {
			   data.push(mp.zheng);
			   for (var i = 0; i < jing.length; i++) {
			       if (mp.jing_l === jing[i]) {
					   findj = true;
			           labels.push(i);
			       }
			   }
			   if (!findj)
			       labels.push(-1);
            });
			N = labels.length;
			test3();
			test2();
			test();
    	}
    });
}

var should_train = false;
var trainer;

function test3() {
    if (fs.existsSync('json.txt')) {
    	var data = fs.readFileSync('json.txt');
    		if (data === null) {
    		    should_train = true;
    		} else { 
       		    net.fromJSON(JSON.parse(data));
                trainer = new convnetjs.SGDTrainer(net, {learning_rate:0.01, momentum:0.1, batch_size:10, l2_decay:0.001});
    		}
        //fs.writeFile('json1.txt', JSON.stringify(net.toJSON()));
    }
}

